var accordions, i, j, len;

for (j = 0, len = (accordions = document.getElementsByClassName("accordion-tab-title")).length; j < len; j++) (i = accordions[j]).addEventListener("click", function() {
    var e;
    this.classList.toggle("is-open"), (e = this.nextElementSibling).style.maxHeight ? e.style.maxHeight = null : e.style.maxHeight = e.scrollHeight + "px";
});